import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import {PlaceService} from "../services/place.service";
import {Place} from "../model/place";
import {LatLngExpression} from "leaflet";

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent implements OnInit {

  public map: any;

  private element: any;
  private centroid: L.LatLngExpression = [41.9981, 21.4254]; //
  // @ts-ignore
  private place: Place[];
  private initMap(): void {
    console.log(this.place);
    this.map = L.map('map', {
      center: this.centroid,
      zoom: 12
    });

    const tiles = L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 18,
      minZoom: 10,
      attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
    });

    // create 5 random jitteries and add them to map
    // const jittery = Array(5).fill(this.centroid).map(
    //   x => [x[0] + (Math.random() - .5)/10, x[1] + (Math.random() - .5)/10 ]
    // ).map(
    //   x => L.marker(x as L.LatLngExpression)
    // ).forEach(
    //   x => x.addTo(this.map)
    // );
    for (let i = 0; i < this.place.length; i++) {
      this.element = this.place.at(i)
      this.initMarkers(this.element);
    }
    tiles.addTo(this.map);
  }
  initMarkers(elemen : any): void{
    for (let i = 0; i < this.place.length; i++) {
      elemen=this.place.at(i)
      // L.marker([elemen.coordinate_x,elemen.coordinate_y] as LatLngExpression)
      Array(1).fill(this.centroid).map(
        x => [elemen.coordinate_x,elemen.coordinate_y]
      ).map(
        x => L.marker(x as L.LatLngExpression)
      ).forEach(
        x => x.addTo(this.map)
      );
    }

  }
  constructor(private placeService: PlaceService) {

  }

  ngOnInit(): void {
    this.placeService.getAllBooks().subscribe(
      (data) => {
        this.place = data
        // console.log(data);
        this.initMap();
      }
    );



  }

}
<div class="map-container">
  <div class="map-frame">
    <div id="map"></div>
  </div>
</div>
