import { Component, OnInit } from '@angular/core';
import { Place } from '../model/place';
import { PlaceService } from '../services/place.service';
import { MarkerServiceService } from '../services/marker-service.service'
import * as L from 'leaflet';
import {icon} from "leaflet";
@Component({
  selector: 'app-place',
  templateUrl: './all.component.html',
  styleUrls: ['./all.component.css']
})
export class AllComponent implements OnInit {

  // @ts-ignore
  place: Place[];
  // @ts-ignore
  map;
  constructor(private placeService: PlaceService,private ms: MarkerServiceService) {
    console.log(this.ms.coords);
    this.ms.coordsChange.subscribe(coords => {
      console.log(coords);
      this.map.flyTo(coords, this.map.getZoom());
      // @ts-ignore
      L.marker(coords, { icon }).addTo(this.map);
    });
  }

  ngOnInit(): void {
    this.placeService.getAllBooks().subscribe(
      (data) => this.place = data

    );

    // this.books.forEach(async(books)=>{
    //
    // })
  }


}
