  </tr>
  </thead>
  <tbody>
  <tr *ngFor="let p of place">
    <td>{{p.name}}</td>
    <td>{{p.amenity}}</td>
    <td>{{p.coordinate_x}}</td>
    <td>{{p.coordinate_y}}</td>
  </tbody>

</table>

